import React, { useState, useEffect } from "react";
import TweetBox from "./TweetBox";
import Post from "./Post";
import "./Feed.css";
import FlipMove from "react-flip-move";
// import axios from "axios";
import { ethers } from "ethers";
import Twitter from "./utils/TweetFactory.json";

import Web3 from "web3";
import { TwitterContractAddress, TweetAbi } from "./utils/config.js";

const web3 = new Web3(Web3.givenProvider || "ws://localhost:8545");
const tweetContract = new web3.eth.Contract(TweetAbi, TwitterContractAddress);

function Feed({ personal }) {
  const [posts, setPosts] = useState([]);

  const getUpdatedTweets = (allTweets, address) => {
    let updatedTweets = [];
    for (let i = allTweets.length - 1; i >= 0; i--) {
      if (allTweets[i].username.toLowerCase() === address.toLowerCase()) {
        let tweet = {
          id: allTweets[i].id,
          tweetText: allTweets[i].tweetText,
          isDeleted: allTweets[i].isDeleted,
          username: allTweets[i].username,
          personal: true,
        };
        updatedTweets.push(tweet);
      } else {
        let tweet = {
          id: allTweets[i].id,
          tweetText: allTweets[i].tweetText,
          isDeleted: allTweets[i].isDeleted,
          username: allTweets[i].username,
          personal: false,
        };
        updatedTweets.push(tweet);
      }
    }
    return updatedTweets;
  };

  const getAllTweets = async () => {
    let allTweets = await tweetContract.methods.getAllTweets().call();
    let updatedTweets = getUpdatedTweets(allTweets, web3.eth.defaultAccount);
    setPosts(updatedTweets);
  };

  const deleteTweet = async (id) => {
    let tx = await tweetContract.methods.deleteTweet(id).send({
      from: web3.eth.defaultAccount,
    });
    console.log(tx);
    getAllTweets();
  };

  useEffect(() => {
    getAllTweets();
  }, []);

  return (
    <div className="feed">
      <div className="feed__header">
        <h2>Home</h2>
      </div>
      <TweetBox />
      <FlipMove>
        {posts.map((post) => (
          <Post
            key={post.id}
            post={post}
            deleteTweet={deleteTweet(post.id)}
            personal={post.personal}
          />
        ))}
      </FlipMove>
    </div>
  );
}

